@js:
var sort = [];
push=(title,link,size)=>sort.push({
    "title": title,
    "url": link,
    "style": {
      "layout_flexGrow": 1,
       "layout_flexBasisPercent": size
    }
  });

push("最近更新","/last",1);

kind=["全部分类",
"武侠仙侠","现代都市","历史军事",
"科幻小说","游戏竞技","恐怖灵异",
"言情小说","动漫同人","其他类型"
].map((t,i)=>{
		if(i==0) s=1;
		else s=0.25;
	u=`/novels/class/${i}_{{page}}.html`;
		push(t, u, s);
		});

