"现代都市","历史军事","科幻小说",
"游戏竞技","恐怖灵异","言情小说",
"动漫同人","其他类型"];

xs=[["完本小说","/full/#"],
["小说排行","/newhot_#_0"]
].map(([title,id],i)=>{
  u=`/novels${id.replace("#"
  ,i)}_{{page}}.htm`;
  push(title, u, 1);
  novel.map((t, i)=>{
    u=`/novels${id.replace("#"
  ,i)}_{{page}}.htm`;
    l=novel.length-1;
    if(i==l) s=1;
		else s=0.25;
		push(t, u, s);
  });
});


