//警告⚠️勿动
function atob(input) {
  const chars = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789+/=';
  let str = String(input).replace(/=+$/, '');
  let output = '';
  if (str.length % 4 === 1) {
    throw new Error('InvalidCharacterError: The string to be decoded is not correctly encoded.');
  }
  for (let bc = 0, bs = 0, buffer, i = 0; buffer = str.charAt(i++); ~buffer && (bs = bc % 4 ? bs * 64 + buffer : buffer, bc++ % 4) ? output += String.fromCharCode(255 & bs >> (-2 * bc & 6)) : 0) {
    buffer = chars.indexOf(buffer);
  }
  return output;
}
function decodeStr(str){
	var htm, j, leng, h, k, l;
	htm = '';
	leng = str['length'];
	for (j = 0x0;j < leng;j++) {
		h = str['charCodeAt'](j);
		if (h <= 0x7f) {
			htm += String['fromCharCode'](h);
		} else {
			k = str['charCodeAt'](++j);
			if (h <= 0xdf) {
				htm += String['fromCharCode'](((h & 0x1f)<< 0x6)| (k & 0x3f));
			} else {
				l = str['charCodeAt'](++j);
				htm += String['fromCharCode']((((h & 0xf) << 0xc)|((k & 0x3f)<< 0x6))|(l & 0x3f));
			}
		}
	}
	return htm;
}
function decode(c){
	data = c.slice(Number(c.slice(4,6))+6,c.length-Number(c.slice(4,6))).replace(/_/g,"8L3A+").replace(/-/g,"PHA+");
	return decodeStr(atob(data));
}

