result="http://www.xxmanmi.com/mp4.html?u="+baseUrl
}else{
	num=baseUrl.match(/playUrl(\d+)\?/)[1]
 if(num=="5"){
result="http://www.xxmanmi.com/mp4.html?u="+(JSON.parse(result).url).replace(/\\/g,'')
 }
 if(num=="6"){
result="http://www.xxmanmi.com/mp4.html?u="+(JSON.parse(result).url).replace(/\\/g,'')
 }
 if(num=="8"){
result="https://danmu.yhdmjx.com/m3u8.php?url="+result
 }
