].map(([title,list],gender)=>{
		gender++
		push("༺ˇ»`ʚ "+title+"生频道 ɞ´«ˇ༻",$$(gender,null,null),1);
		list.map([title,b]=>{
				push("༺ "+title+" ༻",$$(0,b,1),1);
				["热门","评分","字数"].map((title,c)=>{
						c++;
						["["+title+"]","短篇","中篇","长篇"].map((title,a)=>{
								push(title,$$(a,b,c),a==0?0.25:0.15);
							});
					});
			});
	});
"["+sort.toString()+"]";

