bookSourceComment:
/*访问过快偶尔宕机，出错了会重试加载。
以下代码勿动！可在js中通过eval(String(source.bookSourceComment))
调用*/
var error = /503 Service Temporarily/;  
var maxRetries = 30; // 设置最大重试次数  
var retryCount = 0; // 初始化重试次数  

while (error.test(result) && retryCount < maxRetries) {  
    java.log("网站出错，重新加载");  
    cookie.removeCookie(baseUrl);  
    result = java.ajax(baseUrl);  
    retryCount++; // 每次循环后增加重试次数计数器  
}  

if (retryCount === maxRetries) {  
    java.log("重试次数已达到上限，放弃重试");  
} else {  
    result; // 如果循环结束且未达到上限，则返回结果  
}

