<js>
if(result.length > 0){
//java.log(result);
	var info = result[result.length-2].text();
	//java.log("1111111111111111111111111111111111111111111111111111");
//java.log(info);
	var size = parseInt(parseFloat(info.match(/\d*\.\d+(?=\s*MB)/)[0])*1024*512);
	java.put("bookTitle",result[1].attr("title"));
	java.put("bookAuthor",result[2].text());
	java.put("bookKinds",[result[3].text(),result[4].text(),info.match(/\d\d\d\d.\d\d.\d\d/)[0]]);
	java.put("bookcCount",size);
	java.put("lastChaptor","");
	java.put("bookIntro",result[result.length-1].html());
	java.put("bookPic",result[1].getElementsByTag("img")[0].attr("src"));
	java.put("bookTocURL",result[0].attr("href"));
	java.put("bookDownURL",result[result.length-3].attr("href"));
	var asd = java.get("https://www.huangdizhijia.com" + java.get("bookDownURL"), {"method":"POST"}).parse().getElementsByClass("btn-main-stroke");
	for(var i in asd){
		if(asd[i].text().match(/TXT格式下载/)){
			java.put("bookDownURL","https://www.huangdizhijia.com" + asd[i].attr("href"));
			break;
		}
	}
//java.log(java.get("bookDownURL"));

}
result = "";
result;
</js>

