if(web&&html){
	length=p.length>5?3:p.length;
	for(i=0;i<length;i++){
	name=p[i].attr("name")
	value=p[i].attr("value")
	clas=p[i].attr("class")
	if(value!=""){
	add=`${name}=${value}`
	}else if(clas=="text"){
		k=java.encodeURI(key,"gbk")
		add=`${name}=${k}`}
