ruleContent.content:
id.bofang_box@script.0@all##.*url":"(.*)","url_next":.*##$1
@js:
result=unescape(java.base64Decode(result))
result="http://www.qimiqimi.net/jiexi/?url="+result
if(book.durChapterIndex==chapter.index){
		java.startBrowser(result,title)
		java.toast('正在加载视频\n视频加载较慢，请耐心等待')
	}
'【刷新正文】播放\n✅直链:\n'+result

@js:
data=result.match(/data:\s*'(.*?)'/)[1];
nonce=result.match(/<script>window.*?=(.*?)<\/script>/)[1];
nonce=eval(nonce);
var N = String(nonce).match(/\d+\w+/);
jlen=N.length;
while(jlen){
jlen -= 1;
jlocate = parseInt(N[jlen].match(/(\d+)/)[0]) &255;
jstr = N[jlen].replace(/\d+/g,'');
data=data.substring(0,parseInt(jlocate))+data.substring(parseInt(jlocate)+jstr.length,data.length)}
piclist=eval(java.base64Decode(data).match(/"picture":(\[{".*\])/)[1]);
html='';
for(i in piclist){
html += '<img src="'+piclist[i].url+'">\n'
}
html

