try{
var data=java.getString("h1@text");
//java.log(data)
var page=data.match(/\d+.(\d+)/)[1];
var next=[];
if(data&&page){
	for(i=2;i<=page;i++){
		var url=baseUrl.replace(/.html/,`_${i}.html`);
		next.push(url);
		}
		next;
	}
}catch(e){
	java.log(e)
	}finally{next}

