jsLib:
function headUrl(url){
var countArray=url.split('/').filter(n=>n!='')
var lastData= countArray[countArray.length-1]
if(/_([1-9]\d*\.?\d*)|(0\.\d*[1-9]).html/.test(lastData)){
    countArray.splice(countArray.length-1,1)
}
countArray.push('index_1.html')
return countArray.join('/')
}

