@js:
sort=[];
burl=source.getKey().match(/([^#]*)/)[1];
push=(title,url,type1,type2)=>sort.push({
		title: title,
		url: url,
		style: {
				layout_flexGrow: type1,
				layout_flexBasisPercent: type2
			}
	});
	push("精选", `/api/novel/book/v2/plate/book.do?like=0&plateId=302&page={{page-1}}&size=10&t={{Date.now()}}&pkg=com.smart.app.jiudianjiu.xin.leisureNovel`, 1, 0.25);

	function bd(i) {		
			url= burl+`/api/novel/book/v2/top.do?channelId=${i}&pkg=com.smart.app.jiudianjiu.xin.leisureNovel`;
     result=java.ajax(url);
     return  JSON.parse(result).data; 		
		}

	[["男频",bd(1)],["女频",bd(2)]].map([title,category]=>{
	push(title+"❤榜单", null, 1, 1);
 category.map(($)=>{	   	
	    title=$.topName;
     	id=$.topId;
	      	url= `/api/novel/book/v2/top/book.do?topId=${id}&page={{page-1}}&size=10&t={{Date.now()}}&pkg=com.smart.app.jiudianjiu.xin.leisureNovel`;
	push(title, url, 1, 0.25);
        });
  });


	function fl(i) {					
			url= burl+`/api/novel/book/v2/class.do?channelId=${i}&pkg=com.smart.app.jiudianjiu.xin.leisureNovel`;
     result=java.ajax(url);
     return  JSON.parse(result).data; 		
		}


	[["男频",fl(1)],["女频",fl(2)]].map([title,category]=>{
	push(title+"❤分类", null, 1, 1);

 category.map(($)=>{	   	
	    title=$.className;
     	parentClassId=$.classId;
     	fl_url=burl+ `/api/novel/book/v2/childClass.do?classId=${parentClassId}&pkg=com.smart.app.jiudianjiu.xin.leisureNovel`;
     	D=JSON.parse(java.ajax(fl_url)).data;
     	push("🍁"+title+"🍁", null, 1, 1);
   D.map(($,index)=>{	   	
	    title=$.className;
     	classId=$.classId;
	      	url= `/api/novel/book/v2/class/book.do?parentClassId=${parentClassId}&classId=${classId}&status=all&page={{page-1}}&size=10&t={{Date.now()}}&pkg=com.smart.app.jiudianjiu.xin.leisureNovel`;
	      	if(D.length > 3&&D.length!=4){
		if(index+1 <= D.length - D.length%3)
			 {	push(title, url, 1, 0.25);}
				else{ push(title, url, 0, 0.29);}
				}
				else if(D.length == 4){ push(title, url, 1, 0.4);}
				else{ push(title, url, 1, 0.25);}
        });  
     });
  });


JSON.stringify(sort);

