@js:
result ="https://m.weibo.cn/api/container/getIndex?containerid=100103type%3D1%26q%3D{{key}}%26t%3D&page_type=searchall&page={{page}}";

if(/^#.*?#$/.test(key)){
	//话题搜索
	key = key.match(/^#(.*?)#$/)[1];	result=`https://m.weibo.cn/api/container/getIndex?containerid=100103type%3D38%26q%3D${key}%26t%3D&page_type=searchall&page={{page}}`
	}

if(/^%.*?%$/.test(key)){
	//超话搜索
	key = key.match(/^%(.*?)%$/)[1];	result=`https://m.weibo.cn/api/container/getIndex?containerid=100103type%3D98%26q%3D${key}%26t%3D&page_type=searchall&page={{page}}`
	}

if(/^@.*?@$/.test(key)){
	//用户搜索
	key = key.match(/^@(.*?)@$/)[1];	result=`https://m.weibo.cn/api/container/getIndex?containerid=100103type%3D3%26q%3D${key}%26t%3D&page_type=searchall&page={{page}}`
	}

