@js:
sort = [];
push = (title, url, type) => sort.push({
  title: title,
  url: url,
  style: {
    layout_flexGrow: 1,
    layout_flexBasisPercent: type
  }
});
$ = (x) => `search?${x}&orderType=2&page={{page}}&searchType=1&size=40`;
u = source.key;
push("• 排行榜 •", null, 1);
["周榜", "月榜", "年榜", "总榜"].map((p, q) => {
  push(p, `rank?type=${q + 1}`, null);
});
[["题材", "category"],
["标签", "includeTags"]].map([a, b] => {
  push(`• ${a} •`, $(""), 1);
  d = /y/.test(b) ? "categories": "tags";
  c = JSON.parse(java.ajax(u + d)).model;
  for (i = 0; i < c.length; i++) {
    push(
      c[i].name,
      $(b + "=" + encodeURIComponent(c[i].name)),
      null
    );
  };
});
JSON.stringify(sort);

