var result = typeof result !== 'undefined' ? result : {};
function Login() {
    var Account = result['账号：'] || Map('账号：');
    var Password = result['密码：'] || Map('密码：');
    if (Account == '' && Password == '') {
        login((cache.get("uaaAccount") && String(cache.get("uaaAccount")).length > 0) ? ('【登录提示】\n\n┏┅━┅━┅━┅━┅━┅━┅━┅━┅┓\n┋┋' + '\n已退出账号：' + cache.get("uaaAccount") + '\n┋┋' + '\n┗┅━┅━┅━┅━┅━┅━┅━┅━┅┛') : ('【登录提示】' + '\n\n请输入账号和密码'));
        cache.put("uaaAccount","");
        cache.put("uaaId","");
        cache.put("uaaToken","");
        return;
    } else if (Account == '') {
        login('【登录提示】' + '\n\n请输入账号');
        return;
    } else if (Password == '') {
        login('【登录提示】' + '\n\n请输入密码');
        return;
    } else {
            var requestBody = 'loginName=' + Account + '&password=' + Password;
            var headers = '{"Content-Type":"application/x-www-form-urlencoded"}';
            var option = {
                "method": "POST",
                "body": String(requestBody),
                "headers": String(headers)
            };
            Response = java.ajax('https://www.uaa.com/api/console/app/login,' + JSON.stringify(option));
java.log(Response);
            if (JSON.parse(Response).model) {
                cache.put("uaaAccount",Account);
                cache.put("uaaId",JSON.parse(Response).model.id);
                cache.put("uaaToken",JSON.parse(Response).model.token);
                login('【登录提示】\n\n┏┅━┅━┅━┅━┅━┅━┅━┅━┅┓\n┋┋' + '\n账号：' + Account + '\n┋┋' + '\n❤️登录成功❤️\n┋┋' + '\n┗┅━┅━┅━┅━┅━┅━┅━┅━┅┛');
            } else {
                cache.put("uaaAccount","");
                cache.put("uaaId","");
                cache.put("uaaToken","");
                login('【登录提示】' + '\n\n♣️登录失败♣️\n\n请检查账号和密码');
            }
    }
}

function clear() {
	cache.delete("croxy host");
	cache.delete("croxy time");
	cache.delete("croxy ck");
	java.toast("缓存已清空")
}

