loginCheckJs:
var nologinflag = /<div\s*class="title"\s*>.*1234.*/.test(result.body())
java.log("登录检测,需要验证："+nologinflag)
//java.log("请求地址"+baseUrl)
//java.longToast("需要验证："+nologinflag)
if(nologinflag) {
	var url = String(baseUrl + "," + JSON.stringify({
		"charset": "gbk",
		"method": "POST",
		"body": "action=1&v=1234"
	}));
 var loginres = java.ajax(url)
 if(loginres == 'success') nologinflag = loginres;
 //java.toast("自动验证："+nologinflag)
 result = java.getStrResponse()
 java.log("重新获取")
}

//java.log("cookie："+cookie.getCookie(baseUrl))

result

