function getServerId() {
  return String(Math.floor(Math.random() * 100) + 100);
}

function getHost() {
  const { java, cache } = this;
  let i = 0;
  let n = 10; // æœ€å¤§é‡è¯•æ¬¡æ•°
  while (i < n) {
    try {
      let sid = getServerId();
      java.log(`æµ‹è¯•æœåŠ¡å™¨ï¼š${sid}`);
      let su = "https://www.croxy.network/requests?fso=";
      let body = `url=${su}&proxyServerId=${sid}&frontOrigin=https%3A%2F%2Fwww.croxy.network`;
      response = java.post(su, body, {});
      break;
    } catch(e) {
      i++;
    }
  }
  let locationHeader = response.header("location");
  let host = "";
  if (locationHeader) {
    let u = java.toURL(locationHeader);
    host = `https://${u.host}`;
    // å­˜å‚¨cookie
    let cook = `__cpc=${u.searchParams["s"]}`;
    cache.put(`croxy ck`, cook);
    // å­˜å‚¨hostå’Œæ—¶é—´
    let time = Date.now().toString();
    cache.put(`croxy time`, time, 7200);
    cache.put(`croxy host`, host);
  } else {
    java.log("æ¥å£è·å–é”™è¯¯");
  }
  return String(host);
}

function ipCheck() {
  const { java, cache } = this;

  let time = cache.get(`croxy time`);
  let ntime = Date.now().toString();
  let rex = ntime - time;

  if (!cache.get("croxy host") || !time || rex > 2 * 60 * 60 * 1000) {
    Packages.java.lang.Thread.sleep(Math.random() * 1000);
    try {
      if (!cache.get('cxylock')) {
        cache.put('cxylock', 1, 60);
        this.getHost();
        cache.delete('cxylock');
      }
    } catch (error) {
      cache.delete('cxylock');
    }

    while (cache.get('cxylock')) {}

    return cache.get("croxy host");
  } else {
    return cache.get("croxy host");
  }
}

