var urls=result.split('\n');
urls.length=1;
urls=urls.map(c=>"https://www.ccrip.com/"+c);
var imgs=[];
for(var i=0;i<urls.length;i++){
       var ress=java.get(urls[i],{"User-Agent":"Mozilla/5.0 (iPhone; CPU iPhone OS 13_2_3 like Mac OS X) AppleWebKit/605.1.15 (KHTML, like Gecko) Version/13.0.3 Mobile/15E148 Safari/604.1"});
      var html=ress.body();
html=html.replaceAll('\n','').replaceAll('\t','').replaceAll('\r','').replaceAll(' ','');
      html=html.substr(html.indexOf('cp_img">')+9);
      html=html.substring(0,html.indexOf('class="view-bottom-bar'));
      var igs=html.split('"><imgclass');
      for(var j=0;j<igs.length;j++){
            var ig=igs[j];
             ig=ig.substr(ig.indexOf('data-original="')+15);
             if(ig.indexOf('"></')!=-1)   ig=ig.substring(0,ig.indexOf('"></'))
             imgs.push(ig)
      }   
}
var h=imgs.map(d => "<img class='lazy' data-origanl='" + d + "'/>").join('');


result="<html><head><meta charset=\"utf-8\"><meta name=\"viewport\" content=\"width=device-width,initial-scale=1,minimum-scale=1,maximum-scale=1,user-scalable=no\"/><style>html,body,*{margin:0;padding:0;width:100%;}body{height: 100%;}img{width:100%;min-height: 40px;}h1{text-align: center;padding: 2em 0;}</style><script>function scrolled() { var imgs = document.querySelectorAll('.lazy'); for (let i = 0; i < imgs.length; i++) { const img = imgs[i]; if (img.offsetTop < document.body.scrollTop + document.body.offsetHeight + 100) { img.className = ''; img.src = img.attributes['data-origanl'].value } } } function init() { document.body.onscroll = scrolled;scrolled();}</script></head><body onload='init()'>"+h+"</body></html>"
