ruleSearch.bookList:
data.books[*]
@js:JSON.parse(result).map(o=> {
    var J = org.jsoup.Jsoup.parse(java.ajax(baseUrl.match(/(.+?\/\/.+?\/)/)[1] + 'source/' + o.sourceUuid));
    return {
        name: o.title,
        author: o.author,
        tags: o.keyName,
        num:o.showTotalCount+'字',
        latest: String(J.select('.m-book-recent a').text()).replace(/[\(（【].*[求更谢乐发推].*[】）\)]/,'') +String(J.select('.m-book-recent p').text()).replace(/\s*(\d+-\d+-\d+).+/, '（$1）').replace(/\s*(\d+)月(\d+)日.+/, '（$1-$2）'),
        intro: o.description.replace(/<\/?\w+>/g, '').trim(),
        cover: o.coverImage,
        catalogUrl: '/reader/book/info.json?catalog_only=true&source_uuid=' + o.sourceUuid
    }
});

