@js:

//分类样式(0/1)
//0: 排行-类型-状态
//1: 类型-排行-状态

style = 0

sort=[];
push=(title,url,type)=> sort.push({
		title: title,
		url: url,
		style: {
				layout_flexGrow: 1,
				layout_flexBasisPercent: type
			}
	});
$$=(a,b,c)=> `https://www.qhyrzs.com/list/${a}-${b}-0-0-0-0-${c}-0-{{page}\}.html`;

a=b=c=0;
d=(title,a,b,c)=> push("༺ˇ»`ʚ"+title+"ɞ´«ˇ༻",$$(a,b,c),1);

arra=["全部小说","玄幻奇幻","武侠修真","都市青春","穿越历史","网游竞技","科幻科技","恐怖灵异","女生言情","其他类型"];
arrb=[["默认","default"],["总点击","allvisit"],["月点击","monthvisit"],["周点击","weekvisit"],["日点击","dayvisit"],["总推荐","allvote"],["收藏数","goodnum"],["字数","size"],["入站时间","postdate"]];
arrc=(title,a,b)=>[""+title+"","连载","全本"]
		.map((title,c)=>{
		if(c) c+='';
		else c='0';
		push(title,$$(a,b,c),0.25);
			});

if(style==0){
		arrb.map([title,b]=>{
				d(title,a,b,c);
				arra.map((title,a)=>arrc(title,a,b));
			});
	}
else{
		arra.map((title,a)=>{
				d(title,a,b,c);
				arrb.map([title,b]=>arrc(title,a,b));
			});
	}
JSON.stringify(sort);

