function headUrl(url){
        var countArray = url.split('https://www.84kanshu.com/xiaoshuo/');
        if(countArray[1]){
            let num=countArray[1].split('/')[0]
             return 'https://www.84kanshu.com/xiaoshuo/'+num+'/';
        }else{
            return url
        }
}

ruleSearch.bookList:
 .b_algo
 @js:
 bookSourceUrl=String(source.bookSourceUrl).match(/([^\#]+)\#/)[1]
    let a=result;
    java.log('必应数量:'+a.length);
    var b = [];
    for (let i =0;i<a.length;i++) {
        var str =org.jsoup.Jsoup.parse(a[i]);
        var list={};
        let realU=str.select('h2').select('a').attr('href').toString();
        if(!realU.includes(bookSourceUrl) || realU.includes('.html')){
            continue;
        }else{
            list["title"]=str.select('h2').select('a').text().split('-最')[0].split('最新')[0].split('章节')[0].split('-巴士')[0];
            //java.log(list["title"]);
            //java.log(list["author"]);
            list["intro"]=str.select('.b_caption').html();
            //java.log(list["intro"]);
            list["url"] =headUrl(realU);
            //java.log(list["url"]);
            b.push(list);
        }
    }
    java.log('网站数量:'+b.length);
result=b;

