        let realU=str.select('h2').select('a').attr('href').toString();
        if(!realU.includes(bookSourceUrl) || realU.includes('.html')){
            continue;
        }else{
            list["title"]=str.select('h2').select('a').text().split('-最')[0].split('最新')[0].split('章节')[0].split('-巴士')[0];
            //java.log(list["title"]);
            //java.log(list["author"]);
            list["intro"]=str.select('.b_caption').html();
            //java.log(list["intro"]);
            list["url"] =headUrl(realU);
            //java.log(list["url"]);
            b.push(list);
        }
    }
    java.log('网站数量:'+b.length);
result=b;

