}

ruleToc.chapterList:
$.data.list
@js:
//反转目录
var r=result.toArray().reverse();
//初始化列表
var list=[],
url=java.get("url"),
li=java.get("li"),
j=org.jsoup.Jsoup.parse(li),
a=j.select("a");//获取最新
for(i in r){
	name=java.getString("$.name",r[i])
	link=url+java.getString("$.id",r[i])
	list.push({text:name,href:link+".html"})
	}
	//反转添加最新
for(i=a.length-1;i>=0;i--){
	name=a[i].text()
	link=a[i].attr("href")
	list.push({text:name,href:link})
	}
list;

