  url=String(url).replace("1.html","{{page}}.html")
        push(title,url,size)
    };
 push("🌹排行🌹",null,1)
    for(i in b){
        title=b[i].text()
        url=b[i].attr("href")
  url=String(url).replace(".html","<,_{{page}}>.html")
        size=0.25;
        push(title,url,size)
    }

   push("🌹全本🌹",null,1)
    for(i in c){
        title=c[i].text()
        url=c[i].attr("href")
  url=String(url).replace(".html","<,_{{page}}>.html")
        size=0.25;
        push(title,url,size)
    }

}else{
  java.toast("发现列表获取失败！")
}

