var js = `(()=>{
  var classify = document.getElementsByClassName("dispc").item(1);
  var elements = classify.getElementsByTagName("a");
  if(elements.length < 1) return [];
  var classifys = [];
  var length = elements.length - 1;
  for(let i = 1; i < length; ++i) {
    let element = elements.item(i);
    classifys.push({"title": element.innerText, "url": element.getAttribute("href")});
  }
  return classifys;
})();`;
var response = java.get(source.getKey(), {"Referer": source.getKey()});
var elements = JSON.parse(java.webView(response.body(), source.getKey(), js));
var urls = [];
for(let element of elements) {
  urls.push({
    "title": element.title,
    "url": (()=>{
      if(/\/\s*1\s*\/\s*$/.test(element.url)) return element.url.replace(/1\s*\/\s*$/,"{{page}}/");
      return `${element.url.replace(/\/\s*$/,"")}{{(page>1)?"/"+page+"/":"/"}}`;
    })(),
    "style": {"layout_flexBasisPercent":0.24,"layout_flexGrow":0}
  });
}
JSON.stringify(urls);

