function imgUrl(bookUrl) {
  bookUrl = String((bookUrl.length==1)?bookUrl[0]:bookUrl);
  var m = bookUrl.match(/^\s*(?:(?:https?\:\/\/[^\/]+)?\/)?(\d+)\_(\d+)/i);
  if(m==null) return "";
  return `http://img.bxbook.cc/${m[1]}/${m[2]}/${m[2]}s.jpg`;
}

