@js:(()=>{
  if(page!=1) throw "search end";
  var js = `(()=>{
    var search = document.getElementsByClassName("search").item(0);
    var form = search.getElementsByTagName("form").item(0);
    var data = {
      "action":form.getAttribute("action"),
      "method":form.getAttribute("method")||"get",
      "query":""
    };
    var elements = form.childNodes;
    for(let element of elements) {
      let name = element.getAttribute("name");
      if(name == null) continue;
      if(data.query.length>0) data.query += "&";
      data.query += name + "=";
      if(name == "${__search_key__()}") {
        data.query += "${key}";
      } else {
        data.query += element.getAttribute("value")||"";
      }
    }
    return data;
  })();`;
  var response = java.get(source.getKey(), {"Referer": source.getKey()});
  var form = JSON.parse(java.webView(response.body(), source.getKey(), js));
  var option = {
    "charset": "UTF-8",
    "method": form.method
  };
  var query;
  if(form.method.toLowerCase() == "post") {
    option["body"] = form.query;
  } else {
    query = `?${form.query}`;
  }
  return `${form.action}${query||""},${JSON.stringify(option)}`;
})();

