    ["清纯少女", "&tone_id=12"],
    ["磁性青叔", "&tone_id=17"],
]; // 接口数据

// 判断接口是否为空
if (sz == null || sz == "" || isNaN(sz) || sz < 0 || sz > data.length) {
    sz = 0;
    source.setVariable(sz);
    java.toast(data[sz][0]);
} else {
    sz = parseInt(sz); // 将sz转换为整数
}

// 设置保存接口
function set(num) {
    if (num < 0 || num > data.length) {
        java.longToast("无效的接口编号！");
        return;
    }
    var show = "  设置成功\n当前模式：";
    var tips = "";
    var msg = show + data[num][0] + tips;
    java.longToast(msg);
    return source.setVariable(num);
}

// 获取接口显示
function get(sz) {
    if (sz < 0 || sz > data.length) {
        java.longToast("无效的接口编号！");
        return;
    }
    var show = "";
    return java.longToast(show + data[sz][0]);
}

var option = data[sz][1];
option;

