    var option = {
        "method": "POST",
        "body": String(requestBody),
        "headers": String(headers)
    };
    for (i = 0; i < 60; i++) {
        Response = java.ajax(URL + '/login?act=authpoll,' + JSON.stringify(option));
        var list = JSON.parse(Response);
        if (list.code == 0) {
            var mid = list.data.mid;
            var access_token = list.data.access_token;
            var refresh_token = list.data.refresh_token;
            var expire = list.data.cookie_info.cookies[0].expires;
            break;
        }
        sleep(1000);
    }
    var url1 = URL + '/login?act=savekey&mid=' + mid + '&access_key=' + access_token + '&refresh_token=' + refresh_token + '&expire=' + expire;
    java.ajax(url1);
    java.ajax(URL + '/login');
}

