function parseToDOM(str){
   var div = document.createElement("div");
   if(typeof str == "string")
       div.innerHTML = str;
   return div.querySelectorAll("a"); ;
}


function okpage(){
	pageclass = $("#pageclass").value.replace(/(\+|\?)/g,'\\$1');

