  lazy = new IntersectionObserver(
    (entires) => {
      console.log("entires", entires);
      entires.forEach((item) => {
        if (item.isIntersecting) {
          item = item.target;
          item.src = item.getAttribute("data-src");
          lazy.unobserve(item);
        }
      });
    },
    {
