// 接口名称黑名单
black1 = /^\s*(爱尚|🇨🇳v6频道)?\s*$/i;


// 集数链接黑名单
// 例：【|http:\/\/39.134.24】
black2 = /^\s*$/i;

X = 0;
fenlei = '\n\t\t'+[
["南风","https://agit.ai/Yoursmile7/TVBox/raw/branch/master/live.txt"],
