["欧文(失效！)","https://jihulab.com/owen2000wy/owentv/-/raw/main/HP20230319.txt"],

].map(($,i)=>{
		x = $[1]==window.location.href;
		if(x&&i===0) X=2;
		if(x&&i===1) X=3;
		return '<a href="'+$[1]+'"><button'+(x?' class="active"':'')+' style="width:'+(i<3?30:46.5)+'%"><b>'+$[0]+'</b></button></a>';
	}).join('\n\t\t');



jiexi = [//解析列表_开始

["Ⓧ 禁用",""],
