</style>`;



//选中标签
function $(rule){
		return document.querySelectorAll(rule);
	}

//删除active
function omit(A){
		return Array.from(A,a=>a.className='');
	}

//增加active
function active($,i){
		return $[i].className='active';
	}




// 注册点击事件
Add=(a,b)=>Array.from($(a),$=>$.addEventListener('click',b));

// 获取当前事件的button
Btn=(evt)=>{
for(el of evt.path){
		if(el.tagName==="BUTTON")return el;
	}
}



// 选择
function xuan(el,type){
		btn = $('.'+type+' button.active');
		if(el.value==btn[0].value)return;
		omit(btn);
		el.className = 'active';
		return geturl();
	}
function xuan1(evt){
		return xuan(Btn(evt),'jiexi');
	}
function xuan2(evt){
		return xuan(Btn(evt),'jishu');
	}


// 切换
huan=(evt)=>{
		el = Btn(evt);
		index = el.value;
		t1 = '.jiekou button';
		t2 = '.jishu div';
		omit($(t1+'.active,'+t2+'.active'));
		active($(t1),index);
		active($(t2),index);
	}


// 链接
val=(type)=>$('.'+type+' button.active')[0].value.trim();
function geturl(){
		url = val('jiexi') + val('jishu');
		return $('iframe')[0].src = $('p')[0].innerText = url;
	}





//初始化
(()=>{
		active($('.jiexi button'),0);
		active($('.jiekou button'),X);
		active($('.jishu div'),X);
		active($('.jishu div.active button'),0);

		// 注册对应的点击事件
		Add('.jiexi button',xuan1);
		Add('.jiekou button',huan);
		Add('.jishu button',xuan2);

		return geturl();
	})();

