function remove(a,b){
let delIndexList = a; // 要删除的序列
 if (delIndexList.length !== 0) { 
for (let i = delIndexList.length - 1; i >= 0; i--) { b.splice(delIndexList[i], 1) } 
return b
}
}

if(baseUrl=='https://app-cdn.jjwxc.net/bookstore/getFullPage?channel=bhxs&version=3'||baseUrl.match(/novelfree/)){
