jsLib:
//获取源变量
function getData() {
	const { source } = this;
	try {
		return JSON.parse(source.getVariable());
	} catch (err) {
		return {};
	}
}

//保存源变量 setData(data)
function setData(obj) {
	const { source } = this;
	if (typeof obj == "object") {
		source.setVariable(JSON.stringify(obj));
	}
}

function getExcludeTags() {
	let data = this.getData();
	try {
		return data.ExcludeTags.join(",");
	} catch (err) {
		return "";
	}
}

