.book-list li@js:
result.toArray().map(o =>{
    var detail= String(o.select('.book-name').attr('href')).replace(/^\//, baseUrl.match(/(.+\/\/.+?\/)/)[1]);
    var oText = s=>String(o.select(s).text().trim());
    var J = org.jsoup.Jsoup.parse(java.ajax(detail));
    var jText = s=>String(J.select(s).text().trim());
    return {
        name: oText('.book-name'),
        author: oText('.smaller').replace(/\s*作者：(\S+).+/, '$1'),
        tags: [String(J.select('.book-title .tag').toArray().map(tag =>tag.text()).join(' / '),J.select('.book-info > .row .tag').toArray().map(tag =>String(tag.text())).filter(v =>v.length < 5).slice(0, 2).join(','))].join(','),
        num:jText('.book-info-numbers span:eq(0)').replace(/●|总字数：/g, '')+'字',
        latest: oText('.smaller').replace(/.+\D(\d+-\d+-\d+).+\/\s*(.+)/, '$2（$1）').replace(/[\(（【].*[求更谢乐发推].*[】）\)]/,'').replace(/.*(第[^章]+章)/,'$1'),
        intro: jText('.book-brief').replace(/\s*简介：/, '').replace(/[\s\t]+/g, ''),
        cover: o.select('.cover').attr('data-original'),
        catalogUrl: detail
    }
}).filter(v =>v.name);

