	ns=(name=="")?"":java.getStringList(name);
	lcs=(lc=="")?"":java.getStringList(lc);
	infs=(inf=="")?"":java.getStringList(inf);
	cs=(cover=="")?"":java.getStringList(cover);
	hs=(href=="")?"":java.getStringList(href);
	//java.log(ns)

	for(var i=0;i<ns.length;i++){
		    jon={"name":ns[i],
		     "href":hs[i],"info":infs[i],
		     "auth":baseUrl.match(/(http[s]*:\/\/[^/]+)/)[1],
		         "cover":cs[i],"lc":lcs[i]};
		java.log(ns[i]+"||"+hs[i]+"||"+cs[i]+"||"+lcs[i]);
		    lis.push(jon);
  }
	return lis;
}
function getChapterList(cn,cu,ci){
