    u=(cu=="")?"":java.getStringList(cu);
    ii=(ci=="")?"":java.getStringList(ci);

   for(var i=0;i<n.length;i++){
   	   jso={"cn":n[i],"cu":u[i],"ci":ii[i]};

   	 //java.log(JSON.stringify(jso))
   	  cs.push(jso);
   }
   return cs;
	}
//参数说明：
//imgr:图片规则,可为img@src或者@img@html
//wdr:正文文字规则，没有置""
//bsurl:图片src属性不含域名，bsurl填域名
//       否则置""
//isheads:解决图片防盗链，没有填""，否则置
//       {"headers"{"Referer":baseUrl}}
function getContent(imgr,wdr,bsurl,isheads){
	wds=(wdr=="")?"":java.getString(wdr);
	tus=java.getString(imgr);
	var imgs="";
 heads=(isheads=="")?"":","+JSON.stringify(isheads)

	if(/@html/.test(imgr)){
		  	imgs=tus;
		}else{
			imgs=tus.split("\n");
			for(var i=0;i<imgs.length;i++){
				if(imgs[i]!=""){
				  imgs[i]='<img  src="'+
				        	bsurl+imgs[i]+heads+'" />'
				   }
				   java.log(imgs[i])
    }
			 imgs=imgs.join("\n")
		}
		return wds+imgs;
}

