ruleBookInfo.tocUrl:
@js:
index=java.getElements("@@class.chapterList@li").length>9;
if(index){
	var id=baseUrl.match(/\d+/);
	var aid=String(id);
	java.put("aid",aid);
	var url=book.origin+"/mytool/getChapterList/,";
	var img=book.origin+"/mytool/getVerify/";
	var verify=java.getVerificationCode(img);
	var body=`verify=${verify}&aid=${aid}&sbt=提交`;
	var option={
  "body": String(body),
  "method": "POST"
}
	result=url+JSON.stringify(option)
	}else{
	var id=baseUrl.match(/\d+/);
	var aid=String(id);
	java.put("aid",aid);
		result=baseUrl;
		}

ruleContent.content:
@js:
if(/_/.test(baseUrl)){
	var go=java.getElement("script");
	var ok=String(go);
	var jsp=ok.match(/	document.getElementById\('(.*)'\).onclick = function/)[1];
	var html=String("@@#"+jsp+"@html");
	result=java.getString(html);
	}else{	result=java.getString("@@.container@html")
		}
result;

