json=[];
if(baseUrl.match(/•/)){
list=baseUrl.replace(/http.*?wd=\s*/,'').split('•');
for(i in list){
href=list[i].match(/【(.*?)】/)[1];
text=list[i].match(/《(.*?)》/)[1];
json.push({text:text,href:href})
}
}else{
list=baseUrl.replace(/http.*?wd=/,'');
href=list.match(/【(.*?)】/)[1];
text=list.match(/《(.*?)》/)[1];
json.push({text:text,href:href})
}
