//判断是否存在目录
if (flags == null) {
    var list = [];
    list.push({
        m: baseUrl,
        n: "正文"
    });
    list;
} else {
    var regex = /<div class="chapters">([\s\S]*?)上一章/i;
    var matches = regex.exec(result);
    if (matches) {
        var content = matches[1];
    }
    var regex = /<a href="([^"]+)"><i class="fa fa-book"><\/i>([^<]+)<i class="fa fa-headphones"[^>]*><\/i><\/a>/g;
    var list = [];
    var match;
    while ((match = regex.exec(content)) !== null) {
        list.push({
            m: match[1],
            n: match[2].trim()
        });
    }
}

