ruleBookInfo.init:
@js:
result = JSON.parse(result).data.info;
source_url = baseUrl.replace('getBookInfo','getResources');
$ = JSON.parse(java.ajax(source_url)).data.resources;

//初始化
n = 0;
v = String(book.getVariable("custom"));
x = v.match(/^\d+$/)?v:n;
x = parseInt(x)<$.length?x:n;

result.book_uptimeStr = $[x].sourceLastChapterUpdate;
result.book_lastchapter = $[x].sourceLastChapter;
charset = $[x].encoded;
chapterPageUrl = $[x].chapterPageUrl;
result.tocUrl = chapterPageUrl+`,{"charset":"${charset}"}`;


//目录规则使用
chapterPageBeat = $[x].chapterPageBeat.rule;
chapterUrl = $[x].chapterUrl.rule;
chapterName = $[x].chapterName.rule;
java.put("chapterPageBeat",chapterPageBeat);
java.put("chapterUrl",chapterUrl);
java.put("chapterName",chapterName);

//正文规则使用
chapterText=$[x].chapterText.rule.replace(/\s+/g, " ");
chapterText_replace=$[x].chapterText.replace.replace(/=>=>(\s+)?/g, "|");

java.put("chapterText",chapterText);
java.put("chapterText_replace",chapterText_replace);

if($.length>1){
		y = '\n📌使用说明： 根据序号设置书籍变量来切换来源(默认0)\n🎯当前源：序号🔺'+x+'🔻【'+$[x].sourceDesc+'】'+(x==0?' (默认)':'');
for(i in $){
		y += '\n❤序号🔺'+i+'🔻【'+$[i].sourceDesc+'】 \n网站：'+$[i].sourceName+' \n更新时间： '+$[i].sourceLastChapterUpdate+'\n最新章节： '+$[i].sourceLastChapter;
	}
result.book_intro += y
}
result = JSON.stringify(result);

ruleContent.content:
@js:
chapterText = java.get("chapterText");
chapterText_replace = java.get("chapterText_replace");
java.log(chapterText)
try{
content = String(result).replace(/\s+/g, " ").match(chapterText)[1].replace(new RegExp(chapterText_replace,"g"), "");
}
catch(e){content = java.getElement("#content");}

