$.url@js:
let content=""
if(result){
let b64 = java.ajax(result)
let str=java.base64Decode(b64.match(/'(.+?)'/)[1])
let list=JSON.parse(str).p
for (i =0;i<list.length;i++){
content+= list[i]+"\n"
}
}else{
content+="购买后继续阅读本章内容"
}
