 @js:
 function headUrl(url){
       let urlReal = url.replace("https://","").replace("http://","")
       if(urlReal.split("/").length>=3){
        return  "http://"+urlReal.split("/").slice(0,3).join("/")
       }
       return ""
}
  bookSourceUrl=String(source.bookSourceUrl).match(/([^\#]+)\#/)[1]
    let a=result;
    java.log('必应数量:'+a.length);
    var b = [];
    for (let i =0;i<a.length;i++) {
        var str =org.jsoup.Jsoup.parse(a[i]);
        var list={};
        let realU=str.select('h2').select('a').attr('href').toString();
        if(!realU.includes(bookSourceUrl)){
            continue;
        }else{
          if(headUrl(realU)!=""){
             list["title"]=str.select('h2').select('a').text();
             list["intro"]=str.select('.b_caption').html();
             list["url"] =headUrl(realU);
             b.push(list);
           }

        }
    }
    java.log('网站数量:'+b.length);
result=b;

