function login() {}
function api() { 	java.startBrowserAwait(source.bookSourceUrl+'/','首页');
    }
function hqmy() { 	java.startBrowserAwait(source.bookSourceUrl+'/key','密钥');
    }
function vip() { 	java.startBrowserAwait(source.bookSourceUrl+'/coffee',"喝咖啡");
    }
function clear() { 	java.startBrowserAwait(source.bookSourceUrl+'/clear',"清空设备");
    }

function fq_login() { 	var cookie = java.getCookie("fanqienovel.com")
    java.log(cookie)
    if (cookie && cookie != "") {
        java.toast("请不要重复登录，请先退出登录！")
        return false
    }
    try {
        java.startBrowserAwait("https://fanqienovel.com", "登录")
    } catch (e) {
        java.toast(e)
    }
    try {
        cookie.removeCookie("snssdk.com")
    } catch (e) {}
    var cookie = java.getCookie("fanqienovel.com")
    if (!cookie || cookie == "") {
        java.toast("登录失败！")
        return false
    }
    java.toast("登录成功！")
    return true
}


// 退出登录
function logout() {
    cookie.removeCookie("fanqienovel.com");
    cookie.removeCookie("snssdk.com");
    java.toast("退出登录成功");
}

function set(media) {
	if (media==='搜索全部'){
		source.setVariable('');

		} else {
			source.setVariable(media);
			};
	  java.toast("\n已切换至："+media+'\n请重新搜索书籍！');
}


function getmedia() {
	   	let media = source.getVariable();
	   	if(media == '') {
	   		media = '全部来源都搜索';
	   		}
			  java.toast("\n当前搜索范围是："+media);

}

