ruleToc.chapterList:
@js:
var list = [];
if (result.match("开始浏览") == null) { //判断目录
    var regex = /href="(content-.+-\d+.html)">(.*)<\/a>/g;
    var match;
    while ((match = regex.exec(result)) !== null) {
        list.push({
            m: match[1],
            n: match[2]
        })
    }
} else {
    var regex = /href="(.*)" s.*(开始浏览)/;
    var match = result.match(regex);
    list.push({
        m: match[1],
        n: match[2]
    });
}
list

@js: flags = result.match(/chapters/g);
//判断是否存在目录
if (flags == null) {
    var list = [];
    list.push({
        m: baseUrl,
        n: "正文"
    });
    list;
} else {
    var regex = /<div class="chapters">([\s\S]*?)上一章/i;
    var matches = regex.exec(result);
    if (matches) {
        var content = matches[1];
    }
    var regex = /<a href="([^"]+)"><i class="fa fa-book"><\/i>([^<]+)/g;
    var list = [];
    var match;
    while ((match = regex.exec(content)) !== null) {
        list.push({
            m: match[1],
            n: match[2]
        });
    }
}
