		 list.push({
				text: text,
				volume: true
			});			
		}

else{  
		$[i].select("li").forEach(a=>{		
 info =	String(a.select("a").attr("title")).replace(/.*时间：|章节/g,"");
 vip =/chapter-locked/.test(a.select("em"));
			text= a.select("a").text();
			href= a.select("a").attr("href");

				list.push({	
						text: text,
						href: href,
			    	info: info,
			    	vip: vip
					})
			})
	}
}
