function gbkToUtf8(gbkEncodedString) {
  // 去除 % 符号
  let hexString = gbkEncodedString.replace(/%/g, '');

  // 将十六进制字符串转换为字节数组
  let byteArray = new Uint8Array(hexString.match(/[\da-f]{2}/gi).map(function (h) {
    return parseInt(h, 16);
  }));

  // 使用 TextDecoder 以 GBK 解码为字符串
  let decoder = new TextDecoder('gbk');
  let decodedString = decoder.decode(byteArray);

  // 使用 TextEncoder 编码为 UTF-8
  let encoder = new TextEncoder();
  return encoder.encode(decodedString).reduce((str, byte) => str + '%' + byte.toString(16).padStart(2, '0'), '');
}

