fl.map([title1,index1,list]=>{
  [["连载","1"],["完结","2"]].map([title2,status]=>{
	push(title1+"♡"+title2, null, 1, 1);
	  list.map((title,index2)=>{
	  	index=Number(index1) + Number(index2);
	    url= `/shuku/0_${index}_0_${status}_0_{{page}}.html`;
	if(index2 +1<= list.length - list.length%3)
			 {	push(title, url, 1, 0.25);}
				else{ push(title, url, 0, 0.29);}
     	   	});
    	});
	});

bd = [["总点击","allvisit"],["月点击","monthvisit"],["周点击","weekvisit"],["日点击","dayvisit"],["总推荐","allvote"],["月推荐","monthvote"],["周推荐","weekvote"],["日推荐","dayvote"],["总收藏","goodnum"],["字数","size"],["入库","postdate"]];
   	[["连载","1"],["完结","2"]].map([title,id]=>{
	push('榜单'+"♡"+title, null, 1, 1);
	  bd.map(([title,uri],index)=>{
	    index++;
	    url= `/shuku/${uri}_0_0_${id}_0_{{page}}.html`;
	if(index+1 <= bd.length - bd.length%3)
			 {	push(title, url, 1, 0.25);}
				else{ push(title, url, 0, 0.29);}

    	});
	});

JSON.stringify(sort);

